script.body:
		
StringBuilder name = new StringBuilder();
String firsName = entity.getFirstName();
String surname = entity.getLastName();

if (firsName) {
    name.append(firsName);
}

if (surname) {
	name.append(' ');
    name.append(surname);
}

name.append(" (");
name.append(entity.getUsername());
name.append(")");

name.toString();
